for $f1 in /config/sessionAgent[@state='enabled' and starts-with(@hostname,'sa-')]
    for $f2 in /config/localPolicy[sourceRealm/@name=$f1/@realmID and to/@addr='*']
        for $from in $f2/from
            return if(string-length(xs:string(data($from/@addr)))<11 and string-length(xs:string(data($from/@addr)))>6 and starts-with(xs:string(data($from/@addr)), '0') and not(xs:string(data($from/@addr))=xs:string(0)) and not(starts-with(xs:string(data($from/@addr)),'0*'))) then
                for $count in (0 to (if(11-string-length(data($from/@addr))=1) then 9
				else if(11-string-length(data($from/@addr))=2) then 99
				else if(11-string-length(data($from/@addr))=3) then 999
				else if(11-string-length(data($from/@addr))=4) then 9999
				else(1)-1))
                let $addr:= if(11-string-length(data($from/@addr))-string-length(xs:string($count))=1)then fn:concat(data($from/@addr),'0',$count)
					else if (11-string-length(data($from/@addr))-string-length(xs:string($count))=2) then fn:concat(data($from/@addr),'00',$count)
					else if (11-string-length(data($from/@addr))-string-length(xs:string($count))=3) then fn:concat(data($from/@addr),'000',$count)
					else if (11-string-length(data($from/@addr))-string-length(xs:string($count))=4) then fn:concat(data($from/@addr),'0000',$count)
					else(concat(xs:string(data($from/@addr)),$count))
                    return <item addr="{ $addr }"> { $f1/@hostname } { $f1/@ipAddress } { $f1/@realmID }</item>
                    else(<item>{ $from/@addr } { $f1/@hostname } { $f1/@ipAddress } { $f1/@realmID }</item>)
